mapper.select:
SELECT * FROM exchangis_launchable_task t
left join exchangis_launched_task_entity l on t.id = l.id



        AND  t.instance = #{instance}


    AND t.delay_time <= NOW()
    AND l.task_id is NULL


ORDER BY t.delay_time ASC LIMIT #{limitSize};

SELECT t.id, t.name, t.create_time, l.last_update_time, t.engine_type, t.execute_user, t.linkis_job_name, t.linkis_job_content, t.linkis_params, t.rate_params, t.linkis_source, t.labels, t.delay_time, t.delay_count, t.instance, t.job_execution_id, l.commit_version FROM exchangis_launchable_task t INNER JOIN exchangis_launched_task_entity l ON t.id = l.id
AND t.instance = #{instance}
where l.`last_update_time` < #{expireTime} AND l.`status` = #{status} ORDER BY t.delay_time ASC LIMIT #{limitSize};

