mapper.update:
UPDATE 
SET
last_update_time = #{lastUpdateTime},
launchable_task_num = #{launchableTaskNum},
status = #{status}
WHERE job_execution_id = #{jobExecutionId}

UPDATE 
SET
status = #{status},
last_update_time = #{updateTime}
WHERE
job_execution_id = #{jobExecutionId}

UPDATE 

SET
status = #{status},
last_update_time = #{updateTime}


        AND job_execution_id = #{jobExecutionId}


        AND #{updateTime} >= last_update_time


        AND launchable_task_num = #{launchableTaskNum};

UPDATE 

SET
progress = #{totalTaskProgress} / launchable_task_num,
last_update_time = #{updateTime}
WHERE
job_execution_id = #{jobExecutionId}

mapper.select:
SELECT l.job_execution_id, l.name, l.create_time, l.execute_user, l.status, l.progress, l.last_update_time, l.create_user, c.source FROM `exchangis_launched_job_entity` l LEFT JOIN `exchangis_job_entity` c ON l.job_id = c.id
and l.job_execution_id = #{jobExecutionId}
and l.name like concat('%', #{jobName}, '%')
and l.create_user like concat('%', #{loginUser}, '%')
and l.status = #{status}
and l.create_time >= #{launchStartTime}
and l.create_time <= #{launchEndTime}
order by create_time desc limit #{start}, #{size}

SELECT count(*)
FROM



        and job_execution_id = #{jobExecutionId}



        and name like concat('%', #{jobName}, '%')


        and create_user like concat('%', #{loginUser}, '%')


        and status = #{status}


        and create_time >= #{launchStartTime}


        and create_time <= #{launchEndTime}
