mapper.update:
UPDATE 
SET
last_update_time = #{lastUpdateTime},
launchable_task_num = #{launchableTaskNum},
status = #{status}
WHERE job_execution_id = #{jobExecutionId}

UPDATE 
SET
status = #{status},
last_update_time = #{updateTime}
WHERE
job_execution_id = #{jobExecutionId}

UPDATE 

SET
status = #{status},
last_update_time = #{updateTime}


        AND job_execution_id = #{jobExecutionId}


        AND #{updateTime} >= last_update_time


        AND launchable_task_num = #{launchableTaskNum};

UPDATE 

SET
progress = #{totalTaskProgress} / launchable_task_num,
last_update_time = #{updateTime}
WHERE
job_execution_id = #{jobExecutionId}

mapper.select:
SELECT l.job_execution_id, l.name, l.create_time, l.execute_user, l.status, l.progress, l.last_update_time, l.create_user, c.source, p.name as project_name FROM `exchangis_launched_job_entity` l LEFT JOIN `exchangis_job_entity` c ON l.job_id = c.id INNER JOIN `exchangis_project_info` p ON c.project_id = p.id
and l.job_execution_id = #{jobExecutionId}
and l.create_time >= #{launchStartTime}
and l.create_time <= #{launchEndTime}
and l.status = #{status}
and l.name like concat('%', #{jobName}, '%')
order by create_time desc

SELECT l.job_execution_id, l.name, l.create_time, l.execute_user, l.status, l.progress, l.last_update_time, l.create_user, c.source, p.name as project_name FROM `exchangis_launched_job_entity` l LEFT JOIN `exchangis_job_entity` c ON l.job_id = c.id INNER JOIN `exchangis_project_info` p ON c.project_id = p.id
and l.job_execution_id = #{jobExecutionId}
and l.create_time >= #{launchStartTime}
and l.create_time <= #{launchEndTime}
and p.id in (SELECT project_id FROM exchangis_project_user WHERE priv_user=#{loginUser})
and l.status = #{status}
and l.name like concat('%', #{jobName}, '%')
order by create_time desc

SELECT count(*)
FROM



        and job_execution_id = #{jobExecutionId}



        and name like concat('%', #{jobName}, '%')


        and create_user like concat('%', #{loginUser}, '%')


        and status = #{status}


        and create_time >= #{launchStartTime}


        and create_time <= #{launchEndTime}

SELECT

FROM



        AND  instance = #{instance}


    AND `last_update_time` < #{expireTime}
    AND `status` = #{status}


ORDER BY last_update_time ASC LIMIT #{limitSize};
